package com.heroimerlin;import java.util.Scanner;import static com.heroimerlin.constants.Constants.MENU_FOOTER;import static com.heroimerlin.constants.Constants.MENU_HEADER;public class ConsoleLogin {    private User user;    private Scanner scan;    private String getCredentials(){        System.out.println(MENU_HEADER);        System.out.println("Usuario:");        String user = scan.next();        System.out.println("Senha:");        String password = scan.next();        System.out.println(MENU_FOOTER());        return user+":"+password;    }    private void showNotAuthorized(){        System.out.println("\nLOGIN OU SENHA ERRADO!\n");    }    public ConsoleLogin(){        scan = new Scanner(System.in);        boolean keepLoop = true;        String credenrials ;        User u = null;        while (keepLoop){            credenrials = getCredentials();            u = new User(credenrials);            if(u.isAuthenticated())                keepLoop = false;            else                showNotAuthorized();        }        user = u;    }    public User getUser(){        return user;    }}