package com.heroimerlin;import static com.heroimerlin.constants.Constants.ADM_AUTH_LEVEL;import static com.heroimerlin.constants.Constants.VENDEDOR_AUTH_LEVEL;public class User {    private String User;    private String Password;    private int AuthLevel = -1;    private boolean authenticate(){        try {            User u = SingletonDAO.getInstance().getAllUsuariosByUser(this.getUser()).get(0);            if (u.getUser().equals(User) && u.getPass().equals(Password)) {                AuthLevel = u.getAuthLevel();                return true;            }            return false;        }        catch (Exception e){            return false;        }    }    public String getUser(){        return User;    }    public String getPass(){        return Password;    }    public int getAuthLevel(){        if (AuthLevel == -1){            System.out.println("USUARIO NAO AUTENTICADO! RODE authenticate()");            return -1;        }        return AuthLevel;    }    public boolean isAuthenticated(){        return authenticate();    }    public User(String AuthString, int AuthLevel){        this.User = AuthString.split(":")[0];        this.Password = AuthString.split(":")[1];        this.AuthLevel = AuthLevel;    }    public User(String AuthString){        this.User = AuthString.split(":")[0];        this.Password = AuthString.split(":")[1];    }}